const promptTemplate = (requirement: string, schema: Record<string, string>, format: 'json' | 'csv') => `
Generate fake data based on the following business requirement:
"${requirement}"

Use the following schema for the data fields and their types:
${JSON.stringify(schema, null, 2)}

Return the data in ${format} format. Ensure the data is structured and includes all necessary fields. Do not include explanations or additional text, only the data.
`;