import { generateFakeData } from '../helpers/ai/sevices/FakeDataOptions';
const SUPPORTED_FORMATS = ['JSON', 'CSV', 'plain text'];
const isValidFormat = (format: string): boolean => {
    return SUPPORTED_FORMATS.includes(format);
};

const options = {
    purpose: "testing",
    format: "JSON",
    fields: ["UUID","name", "age"],
    volume: 1000,
    constraints: "age between 18 and 20, email in example@domain.com format",
};

if (!isValidFormat(options.format)) {
    console.error(`Invalid format provided: ${options.format}`);
} else {
    generateFakeData(options)
        .then(data => console.log(data))
        .catch(error => console.error("Failed to generate data:", error));
}