import { ScenarioGenerator } from '../helpers/ai/sevices/ScenarioGenerator';
(async () => {
  try {
    const generator = new ScenarioGenerator('YGRYAxxeTpWW4OBBdthd3s8EKEtd1j6WVWQwDv7J');

    const requirement = await generator.askQuestion(
      'Enter your requirement for the test scenario:\n(e.g., "User can log in with valid credentials, System shows an error for invalid credentials, Password is case-sensitive")\n\n> '
    );

    if (!requirement.trim()) {
      console.log('‚ùå No requirement provided. Exiting.');
      return;
    }

    await generator.saveBDDScenarioToFile(requirement, 'login_test', 'src/helpers/ai/features');
  } catch (error) {
    console.error('Unexpected error:', error);
  }
})();


