import { StepDefinitionGenerator } from '../helpers/ai/sevices/StepDefinitionGenerator';

// Example usage with updated file paths
const featureFilePath = 'src/helpers/ai/features/login_test.feature';
const outputFilePath = 'src/helpers/ai/steps/loginSteps.ts';

(async () => {
  const generator = new StepDefinitionGenerator('YGRYAxxeTpWW4OBBdthd3s8EKEtd1j6WVWQwDv7J'); // Pass API key

  const steps = generator.parseFeatureFile(featureFilePath);
  if (steps.length > 0) {
    await generator.generateStepDefinitions(steps, outputFilePath);
  } else {
    console.log('No steps found in the feature file.');
  }
})();


